module.exports = function (url) {
    var resolver = require('./resolver');

    return {
        get: function (site, callback) {
            var o = resolver(site);

            if (o.get) {
                o.get(url, callback);
            }
        },

        share: function (site, params) {
            var o = resolver(site);

            if (o.share) {
                o.share(params);
            }
        }
    };
};
