module.exports = function (settings) {
    var xhr = new XMLHttpRequest();

    xhr.open('GET', settings.url, true);

    xhr.send();

    xhr.onreadystatechange = function() {
        if (this.readyState !== 4) {
            return;
        }

        if (this.status !== 200) {
            if (settings.error) {
                settings.error(this);
            }
        } else {
            if (settings.success) {
                settings.success(this);
            }
        }
    };
};
